import React, { useState } from "react";
import { Container, Typography, TextField, Button, Box, Paper, CircularProgress, Grid, Card, CardContent } from "@mui/material";
import axios from "axios";
import ReactMarkdown from "react-markdown";

const HAT_COLORS = [
  { color: "흰색", eng: "White", desc: "정보, 사실, 데이터" },
  { color: "빨간색", eng: "Red", desc: "감정, 직관, 느낌" },
  { color: "검은색", eng: "Black", desc: "비판적 사고, 위험, 문제점" },
  { color: "노란색", eng: "Yellow", desc: "긍정적 사고, 장점, 기회" },
  { color: "초록색", eng: "Green", desc: "창의적 사고, 아이디어, 대안" },
  { color: "파란색", eng: "Blue", desc: "사고 관리, 결론, 진행방향" },
];

export default function App() {
  const [problem, setProblem] = useState("");
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [answers, setAnswers] = useState(null);
  const [error, setError] = useState("");
  const [copySuccess, setCopySuccess] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setAnswers(null);
    setCopySuccess("");
    try {
      const res = await axios.post("/api/solve", { problem, email });
      // hat_answers를 answers로 세팅
      setAnswers(res.data.hat_answers || res.data);
    } catch (err) {
      setError("서버 오류 또는 네트워크 문제입니다.");
    }
    setLoading(false);
  };

  // 결과 전체 복사 기능
  const handleCopy = () => {
    if (!answers) return;
    let text = HAT_COLORS.map(
      (hat) => `■ ${hat.color} 모자 (${hat.eng} Hat): ${hat.desc}\n` + (answers[hat.eng.toLowerCase()] || "답변 없음")
    ).join("\n\n");
    navigator.clipboard.writeText(text).then(() => setCopySuccess("복사 완료!"));
  };

  return (
    <Container maxWidth="sm" sx={{ py: 5 }}>
      <Paper elevation={4} sx={{ p: 4, borderRadius: 3, mt: 4 }}>
        <Typography variant="h4" align="center" fontWeight={700} gutterBottom color="#1976d2">
          Six Thinking Hats AI 상담
        </Typography>
        <Typography align="center" color="text.secondary" gutterBottom>
          문제를 입력하면 여섯 색깔 모자별 전문가가 심리학적으로 분석해 드립니다.
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <TextField
            label="문제/상황을 입력하세요"
            multiline
            minRows={3}
            fullWidth
            required
            value={problem}
            onChange={e => setProblem(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            label="이메일 (결과 수신)"
            type="email"
            fullWidth
            required
            value={email}
            onChange={e => setEmail(e.target.value)}
            sx={{ mb: 3 }}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth size="large" disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "AI 분석 요청"}
          </Button>
        </Box>
        {error && <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>}
        {answers && (
          <Box sx={{ mb: 2, textAlign: "right" }}>
            <Button variant="outlined" color="primary" onClick={handleCopy} sx={{ mr: 1 }}>
              결과 전체 복사
            </Button>
            {copySuccess && <span style={{ color: '#388e3c', marginLeft: 8 }}>{copySuccess}</span>}
          </Box>
        )}
        {answers && (
          <Box sx={{ mt: 2 }}>
            <Grid container spacing={2}>
              {HAT_COLORS.map((hat) => (
                <Grid item xs={12} sm={6} md={4} key={hat.eng}>
                  <Card sx={{ borderLeft: `8px solid ${
                    hat.eng === "White"
                      ? "#bdbdbd"
                      : hat.eng === "Red"
                      ? "#e57373"
                      : hat.eng === "Black"
                      ? "#212121"
                      : hat.eng === "Yellow"
                      ? "#ffd600"
                      : hat.eng === "Green"
                      ? "#43a047"
                      : "#1565c0"
                  }` }}>
                    <CardContent>
                      <Typography variant="subtitle1" fontWeight={700} color="text.primary">
                        {hat.color} 모자 ({hat.eng} Hat): {hat.desc}
                      </Typography>
                      <Typography variant="body1" sx={{ whiteSpace: "pre-line", mt: 1 }}>
                        <ReactMarkdown>
                          {answers[hat.eng.toLowerCase()] || "답변 없음"}
                        </ReactMarkdown>
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}
      </Paper>
    </Container>
  );
}
