import React, { useState, useRef } from "react";
import { Container, Typography, TextField, Button, Box, Paper, CircularProgress, Grid, Card, CardContent } from "@mui/material";
import axios from "axios";
import ReactMarkdown from "react-markdown";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import royalBotLogo from "./royalbot_logo.png";

const HAT_COLORS = [
  { color: "흰색", eng: "White", desc: "정보, 사실, 데이터" },
  { color: "빨간색", eng: "Red", desc: "감정, 직관, 느낌" },
  { color: "검은색", eng: "Black", desc: "비판적 사고, 위험, 문제점" },
  { color: "노란색", eng: "Yellow", desc: "긍정적 사고, 장점, 기회" },
  { color: "초록색", eng: "Green", desc: "창의적 사고, 아이디어, 대안" },
  { color: "파란색", eng: "Blue", desc: "사고 관리, 결론, 진행방향" },
];

export default function App() {
  const [problem, setProblem] = useState("");
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [answers, setAnswers] = useState(null);
  const [error, setError] = useState("");
  const [copySuccess, setCopySuccess] = useState("");
  const resultRef = useRef();

  // PDF 저장 기능 개선: 이미지 분할 없이 한 번만 addImage, 여러 페이지 분할 로직 정상화
  const handlePdf = () => {
    if (!answers) return;
    const now = new Date();
    const pad = n => n.toString().padStart(2, '0');
    const keyword = problem ? problem.replace(/[^\w가-힣]+/g, " ").trim().slice(0, 10) : "SixThinkingHats";
    const timestamp = `${now.getFullYear()}${pad(now.getMonth()+1)}${pad(now.getDate())}_${pad(now.getHours())}${pad(now.getMinutes())}`;
    const filename = `${keyword}_${timestamp}.pdf`;
    html2canvas(resultRef.current, { scale: 2, backgroundColor: null, useCORS: true, windowWidth: 1200 }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({ orientation: 'p', unit: 'mm', format: 'a4' });
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const imgProps = pdf.getImageProperties(imgData);
      const imgWidth = pageWidth - 20;
      const imgHeight = (imgProps.height * imgWidth) / imgProps.width;

      // 상단 여백, 로고, 타이틀
      pdf.setFillColor(240, 244, 252);
      pdf.rect(0, 0, 210, 297, 'F');
      pdf.addImage(royalBotLogo, 'PNG', 15, 10, 25, 25);
      pdf.setFontSize(22);
      pdf.setTextColor(55, 80, 140);
      pdf.text('Six Thinking Hats AI 분석 결과', 45, 25);

      let y = 40;
      let remainingHeight = imgHeight;
      let position = 0;
      while (remainingHeight > 0) {
        const pageImgHeight = Math.min(remainingHeight, pageHeight - y);
        pdf.addImage(
          imgData,
          'PNG',
          10,
          y,
          imgWidth,
          pageImgHeight,
          undefined,
          'FAST',
          0,
          (position * imgProps.height) / imgHeight,
          imgProps.width,
          (pageImgHeight * imgProps.height) / imgHeight
        );
        remainingHeight -= (pageHeight - y);
        position += pageImgHeight;
        if (remainingHeight > 0) {
          pdf.addPage();
          pdf.setFillColor(240, 244, 252);
          pdf.rect(0, 0, 210, 297, 'F');
          y = 10;
        }
      }
      pdf.save(filename);
    });
  };

  // PDF 저장 및 이메일 첨부 전송 기능
  const handlePdfAndEmail = async () => {
    if (!answers) return;
    const now = new Date();
    const pad = n => n.toString().padStart(2, '0');
    const keyword = problem ? problem.replace(/[^\w가-힣]+/g, " ").trim().slice(0, 10) : "SixThinkingHats";
    const timestamp = `${now.getFullYear()}${pad(now.getMonth()+1)}${pad(now.getDate())}_${pad(now.getHours())}${pad(now.getMinutes())}`;
    const filename = `${keyword}_${timestamp}.pdf`;
    html2canvas(resultRef.current, { scale: 2, backgroundColor: null, useCORS: true, windowWidth: 1200 }).then(async canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({ orientation: 'p', unit: 'mm', format: 'a4' });
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const imgProps = pdf.getImageProperties(imgData);
      const imgWidth = pageWidth - 20;
      const imgHeight = (imgProps.height * imgWidth) / imgProps.width;
      pdf.setFillColor(240, 244, 252);
      pdf.rect(0, 0, 210, 297, 'F');
      pdf.addImage(royalBotLogo, 'PNG', 15, 10, 25, 25);
      pdf.setFontSize(22);
      pdf.setTextColor(55, 80, 140);
      pdf.text('Six Thinking Hats AI 분석 결과', 45, 25);
      let y = 40;
      let remainingHeight = imgHeight;
      let position = 0;
      while (remainingHeight > 0) {
        const pageImgHeight = Math.min(remainingHeight, pageHeight - y);
        pdf.addImage(
          imgData,
          'PNG',
          10,
          y,
          imgWidth,
          pageImgHeight,
          undefined,
          'FAST',
          0,
          (position * imgProps.height) / imgHeight,
          imgProps.width,
          (pageImgHeight * imgProps.height) / imgHeight
        );
        remainingHeight -= (pageHeight - y);
        position += pageImgHeight;
        if (remainingHeight > 0) {
          pdf.addPage();
          pdf.setFillColor(240, 244, 252);
          pdf.rect(0, 0, 210, 297, 'F');
          y = 10;
        }
      }
      // PDF를 Blob으로 변환
      const pdfBlob = pdf.output('blob');
      const formData = new FormData();
      formData.append('pdf', pdfBlob, filename);
      formData.append('email', email);
      formData.append('problem', problem);
      formData.append('answers', JSON.stringify(answers));
      // 백엔드로 전송 (이메일 첨부)
      await fetch('/api/send_email_with_pdf', {
        method: 'POST',
        body: formData
      });
      // 기존처럼 다운로드도 진행
      pdf.save(filename);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setAnswers(null);
    setCopySuccess("");
    try {
      const res = await axios.post("/api/solve", { problem, email });
      // hat_answers를 answers로 세팅
      setAnswers(res.data.hat_answers || res.data);
    } catch (err) {
      setError("서버 오류 또는 네트워크 문제입니다.");
    }
    setLoading(false);
  };

  // 결과 전체 복사 기능
  const handleCopy = () => {
    if (!answers) return;
    let text = HAT_COLORS.map(
      (hat) => `■ ${hat.color} 모자 (${hat.eng} Hat): ${hat.desc}\n` + (answers[hat.eng.toLowerCase()] || "답변 없음")
    ).join("\n\n");
    navigator.clipboard.writeText(text).then(() => setCopySuccess("복사 완료!"));
  };

  return (
    <Container maxWidth="sm" sx={{ py: 5 }}>
      <Paper elevation={4} sx={{ p: 4, borderRadius: 3, mt: 4 }}>
        <Typography variant="h4" align="center" fontWeight={700} gutterBottom color="#1976d2">
          Six Thinking Hats AI 상담
        </Typography>
        <Typography align="center" color="text.secondary" gutterBottom>
          문제를 입력하면 여섯 색깔 모자별 전문가가 심리학적으로 분석해 드립니다.
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <TextField
            label="문제/상황을 입력하세요"
            multiline
            minRows={3}
            fullWidth
            required
            value={problem}
            onChange={e => setProblem(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            label="이메일 (결과 수신)"
            type="email"
            fullWidth
            required
            value={email}
            onChange={e => setEmail(e.target.value)}
            sx={{ mb: 3 }}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth size="large" disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "AI 분석 요청"}
          </Button>
        </Box>
        {error && <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>}
        {answers && (
          <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
            <Button onClick={handleCopy} variant="outlined" color="primary" sx={{ mr: 1 }}>
              결과 전체 복사
            </Button>
            <Button onClick={handlePdf} variant="outlined" color="info" sx={{ mr: 1 }}>
              PDF로 저장
            </Button>
            <Button onClick={handlePdfAndEmail} variant="contained" color="secondary">
              PDF 첨부 이메일 전송
            </Button>
            {copySuccess && <span style={{ color: '#388e3c', marginLeft: 12, alignSelf: 'center' }}>{copySuccess}</span>}
          </Box>
        )}
        {answers && (
          <Box sx={{ mt: 5 }}>
            <div ref={resultRef} style={{ background: '#f0f4fc', borderRadius: 18, padding: 24, boxShadow: '0 2px 12px #0001' }}>
              <Grid container spacing={2}>
                {HAT_COLORS.map((hat, idx) => (
                  <Grid item xs={12} key={hat.eng}>
                    <Card
                      elevation={4}
                      sx={{
                        borderLeft: `8px solid ${
                          hat.eng === "White"
                            ? "#e0e0e0"
                            : hat.eng === "Red"
                            ? "#ef9a9a"
                            : hat.eng === "Black"
                            ? "#212121"
                            : hat.eng === "Yellow"
                            ? "#ffd600"
                            : hat.eng === "Green"
                            ? "#43a047"
                            : "#1565c0"
                        }`,
                        minHeight: 200,
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'flex-start',
                        bgcolor: '#fff',
                        ...(problem.includes('어린이') || problem.includes('초등') ? { bgcolor: '#ffe4fa' } :
                          problem.includes('청소년') || problem.includes('10대') ? { bgcolor: '#e4faff' } :
                          problem.includes('노인') || problem.includes('고령') ? { bgcolor: '#fdf6e3' } : {})
                      }}
                    >
                      <CardContent>
                        <Typography variant="subtitle1" fontWeight={700} color="text.primary">
                          {hat.color} 모자 ({hat.eng} Hat): {hat.desc}
                        </Typography>
                        <Typography variant="body1" sx={{ whiteSpace: "pre-line", mt: 1 }}>
                          <ReactMarkdown>
                            {answers[hat.eng.toLowerCase()] || "답변 없음"}
                          </ReactMarkdown>
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </div>
          </Box>
        )}
      </Paper>
    </Container>
  );
}
